/* Function name: describe_the_weather
 * Description:   This function generates a passive message for players
 *                when they type "check weather", describing to them
 *                what generally the weather looks like in the room.
 *                Example:  "The Sun is shining brightly upon the plains."
 *                
 *                The player checking the weather here is this_player()
 *
 * Arguments:     int weather:  The weather currently in the room.
 *                int terrain:  The terrain of the room.
 * Returns:       1/0
 */
int
describe_the_weather(int weather, int terrain)
{
    if (!terrain)
	return 0;
    switch(weather)
    {
    case SUNNY:
	sunny_weather_description(terrain);
	break;
    case CLOUDY:
	cloudy_weather_description(terrain);
	break;
    case PTCLOUDY:
	ptcloudy_weather_description(terrain);
	break;
    case SUN_WIND:
	sunnywind_weather_description(terrain);
	break;
    case CL_WIND:
	cloudywind_weather_description(terrain);
	break;
    case PTCL_WIND:
	ptcloudy_weather_description(terrain);
	break;
    case RAINBOW:
	rainbow_weather_description(terrain);
	break;
    case FOG:
	fog_weather_description(terrain);
	break;
    case SUNSHOWER:
	sunshower_weather_description(terrain);
	break;
    case SHOWERS:
	shower_weather_description(terrain);
	break;
    case TSTORM:
	thunderstorm_weather_description(terrain);
	break;
    case SNOW_CALM:
	snowcalm_weather_description(terrain);
	break;
    case SNOW_WIND:
	snowwind_weather_description(terrain);
	break;
    case FLURRY:
	flurry_weather_description(terrain);
	break;
    case SNOWING:
	snowing_weather_description(terrain);
	break;
    case BLIZZARD:
	blizzard_weather_description(terrain);
	break;
    default:
	return 0;
    }
    return 1;
}

