/* Function name: display_weather_message
 * Description:   Generates a tell to the player displaying to them
 *                What the weather is like.
 *                Example:  "A flash of lightning strikes a nearby tree."
 *
 * Arguments:     object *players:  An array of players that
 *                                  are experiencing similar weather.     
 *                int weather:      The weather they are experiencing.
 */
void
display_weather_message(object *players, int weather)
{
    int i, terrain;

    i = -1;
    while(++i < sizeof(players))
    {
	terrain = environment(players[i])->query_prop(ROOM_I_TERRAIN_TYPE);
	switch(weather)
	{
	case SUNNY:
	    sunny_weather_tell(players[i], terrain);
	    break;
	case CLOUDY:
	    cloudy_weather_tell(players[i], terrain);
	    break;
	case PTCLOUDY:
	    ptcloudy_weather_tell(players[i], terrain);
	    break;
	case SUN_WIND:
	    sunnywind_weather_tell(players[i], terrain);
	    break;
	case CL_WIND:
	    cloudywind_weather_tell(players[i], terrain);
	    break;
	case PTCL_WIND:
	    ptcloudywind_weather_tell(players[i], terrain);
	    break;
	case RAINBOW:
	    rainbow_weather_tell(players[i], terrain);
	    break;
	case FOG:
	    fog_weather_tell(players[i], terrain);
	    break;
	case SUNSHOWER:
	    sunshower_weather_tell(players[i], terrain);
	    break;
	case SHOWERS:
	    shower_weather_tell(players[i], terrain);
	    break;
	case TSTORM:
	    thunderstorm_weather_tell(players[i], terrain);
	    break;
	case SNOW_CALM:
	    snowcalm_weather_tell(players[i], terrain);
	    break;
	case SNOW_WIND:
	    snowwind_weather_tell(players[i], terrain);
	    break;
	case FLURRY:
	    flurry_weather_tell(players[i], terrain);
	    break;
	case SNOWING:
	    snowing_weather_tell(players[i], terrain);
	    break;
	case BLIZZARD:
	    blizzard_weather_tell(players[i], terrain);
	    break;
	default:
	    setuid(); seteuid(getuid());
	    log_file("BAD_WEATHER", ctime(time()) +" an invalid weather "+
	      "bit "+weather+" was defined for region "+
	      environment(players[i])->query_weather_region() +
	      " in "+file_name(environment(players[i])) + ".\n");
	}
    }
}
